    public AlertaFacturasPP(int cantidadPP, int cantidadPorVencer) {
        initComponents();
        Image icon = Toolkit.getDefaultToolkit().getImage("src\\img\\coffee-heart-original.png");  
        this.setIconImage(icon);
        this.setTitle("Facturas PP");        
        Background.setBackground(BackgroundColor);
        txt_titulo.setForeground(textColor);
        MinPanel.setBackground(BackgroundColor);
        ClosePanel.setBackground(BackgroundColor);
        lbl_porVencer.setForeground(textColor);
        btn_masTarde.setBackground(PanelColor);
        btn_verPP.setBackground(PanelColor);
        lbl_masTarde.setForeground(BackgroundColor);
        lbl_revisarDocumentos.setForeground(BackgroundColor);
        
        
        

        
        lbl_porVencer.setText(lbl_porVencer.getText().replace("{}", String.valueOf(cantidadPP)));
        if(cantidadPorVencer > 0){
            lbl_vencidos.setText(lbl_vencidos.getText().replace("{}", String.valueOf(cantidadPorVencer)));
            lbl_vencidos.setVisible(true);
        }
        else
            lbl_vencidos.setVisible(false);
        
    }
    
    


