    private void lbl_masTardeMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lbl_masTardeMouseReleased
        if(btn_masTarde.contains(evt.getPoint())) btn_masTarde.setBackground(mouseEnterPanelColor);
        else btn_masTarde.setBackground(mouseExitPanelColor);
    }//GEN-LAST:event_lbl_masTardeMouseReleased

    private void lbl_masTardeMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lbl_masTardeMousePressed
        if(evt.getButton() == 1){
            btn_masTarde.setBackground(mouseClickPanelColor);
            this.dispose();
        }
    }//GEN-LAST:event_lbl_masTardeMousePressed

